import React, { Component } from "react";
import "./logoBanner.css";

class SignUp extends Component {
  state = {};

handleSignUp(){
	var email = document.getElementById('email_id').value; 
    	var password = document.getElementById('password_id').value; 
    	var username = document.getElementById('username_id').value; 

     	document.getElementById('username_id').value = ""; 
     	document.getElementById('password_id').value = ""; 
     	document.getElementById('email_id').value = ""; 

     	var data = JSON.stringify({"username":username, "password":password, "email":email});

     	var xhttp = new XMLHttpRequest();
     	xhttp.open("POST", "/api/signup" , true);
     	xhttp.setRequestHeader("Content-Type", "application/json");
     	xhttp.onreadystatechange = function () {
        if(this.readyState === 4 && this.status === 200) {
        	var response = JSON.parse(this.responseText);
        	console.log(response);
          	if (response.status === 'okay') {
            		localStorage.account = response.account;
            		window.location.replace("/profile/" + response.account)
          	}
        }
};
xhttp.send(data);
};
  render() {
    return (
        <div className="sign_up_side">  
        <h1 className= 'login-sign-up' >Sign Up</h1>



        <div class="label-div">
        <label className='login-labels'> Username   </label>
        <input className='label-inputs' type="text" id="username_id"/>
        </div>


        <div class="label-div">
        <label className='login-labels' > Email address  </label>
        <input className='label-inputs' type="text" id="email_id"/>
        </div>

        <div class="label-div">
        <label className='login-labels' > Password  </label>
        <input className='label-inputs' type="password" id="password_id"/>
        </div>



        <div>
          	<button className="submit-buttons" id ="signup_button" onClick={() => this.handleSignUp()}>Submit</button>{' '}
          </div>

      </div>
    );
  }
}

export default SignUp;
