import React, { Component } from "react";
import "./login.css";

class SignUp extends Component {
  state = {};

  handleSignUp() {
	console.log("hello");
    //var email = document.getElementById('userEmail').value; 
	var email = "hello";
	var password = "hello";
    //var password = document.getElementById('userPwd').value; 
    var username = document.getElementById('userName').value; 

    document.getElementById('userEmail').value = ""; 
    document.getElementById('userPwd').value = ""; 
    document.getElementById('userName').value = ""; 

    var obj = JSON.stringify({"username":username, "password":password, "email":email});

    var xhttp = new XMLHttpRequest();
    xhttp.open("POST", "/api/signup" , true);
    xhttp.setRequestHeader("Content-Type", "application/json");

    //  var response = JSON.parse(this.responseText);
    //  console.log(response);
     xhttp.onreadystatechange = function () {
        if(this.readyState === 4 && this.status === 200) {

          var response = JSON.parse(this.responseText);
          console.log(response);

          if (response.status === 'okay') {
            localStorage.token = response.token;
            localStorage.account = response.account;
            window.location.replace("/profile/" + response.account)
          }


        }
     };

     xhttp.send(obj);
  }

  render() {
    return (
      <div className={"backStyle"}>
        <h1>Sign Up: </h1>
        <form>
          <fieldset>
            <div>
              <label>Enter Your Name:</label>
              <input type="text" id="userName" />
            </div>
            <div>
              <label>Enter Your Email:</label>
              <input type="text" id="userEmail" />
            </div>
            <div>
              <label>Enter A Password:</label>
              <input type="password" id="userPwd" />
            </div>
          </fieldset>

	  <div>
	  	<button className="submit_button" id ="btn1" onClick={() => this.handleSignUp()}>Sign Up</button>{' '}
	  </div>
        </form>
      </div>
    );
  }
}

export default SignUp;
